import React, { useState } from 'react';
import './Contact.css';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const [formStatus, setFormStatus] = useState({
    submitted: false,
    error: false,
    message: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Валидация формы
    if (!formData.name || !formData.email || !formData.message) {
      setFormStatus({
        submitted: true,
        error: true,
        message: 'Пожалуйста, заполните все обязательные поля'
      });
      return;
    }

    // Здесь в будущем можно добавить отправку данных через API
    console.log('Form submitted:', formData);

    // Имитация успешной отправки
    setFormStatus({
      submitted: true,
      error: false,
      message: 'Сообщение успешно отправлено! Я свяжусь с вами в ближайшее время.'
    });

    // Сброс формы
    setFormData({
      name: '',
      email: '',
      subject: '',
      message: ''
    });
  };

  return (
    <div className="contact-container">
      <h1 className="section-title">Контакты</h1>
      <p className="section-subtitle">Свяжитесь со мной</p>

      <div className="contact-content">
        <div className="contact-grid">
          <div className="contact-info">
            <h3>Контактная информация</h3>
            <p>Вы можете связаться со мной по указанным каналам связи или заполнить форму.</p>

            <div className="contact-item">
              <i className="fas fa-envelope"></i>
              <a href="mailto:doroshov.daniil69@gmail.com">doroshov.daniil69@gmail.com</a>
            </div>
            <div className="contact-item">
              <i className="fab fa-telegram"></i>
              <a href="https://t.me/ananasDDA" target="_blank" rel="noopener noreferrer">@ananasDDA</a>
            </div>
            <div className="contact-item">
              <i className="fab fa-github"></i>
              <a href="https://github.com/ananasDDA" target="_blank" rel="noopener noreferrer">ananasDDA</a>
            </div>
          </div>

          <div className="contact-form-container">
            <h3>Отправить сообщение</h3>

            {formStatus.submitted && (
              <div className={`form-message ${formStatus.error ? 'error' : 'success'}`}>
                {formStatus.message}
              </div>
            )}

            <form className="contact-form" onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="name">Имя *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="email">Email *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="subject">Тема</label>
                <input
                  type="text"
                  id="subject"
                  name="subject"
                  value={formData.subject}
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <label htmlFor="message">Сообщение *</label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  rows="5"
                  required
                ></textarea>
              </div>

              <button type="submit" className="btn btn-primary">Отправить сообщение</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
