import React, { useState } from 'react';
import './Contact.css';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const [formErrors, setFormErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const validateForm = () => {
    let errors = {};

    if (!formData.name.trim()) {
      errors.name = 'Пожалуйста, введите ваше имя';
    }

    if (!formData.email.trim()) {
      errors.email = 'Пожалуйста, введите ваш email';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'Пожалуйста, введите корректный email';
    }

    if (!formData.subject.trim()) {
      errors.subject = 'Пожалуйста, укажите тему сообщения';
    }

    if (!formData.message.trim()) {
      errors.message = 'Пожалуйста, введите сообщение';
    }

    return errors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const errors = validateForm();
    setFormErrors(errors);

    if (Object.keys(errors).length === 0) {
      setIsSubmitting(true);

      // Имитация отправки данных на сервер
      setTimeout(() => {
        console.log('Form data submitted:', formData);
        setIsSubmitting(false);
        setSubmitMessage({
          type: 'success',
          text: 'Спасибо! Ваше сообщение отправлено.'
        });

        // Сброс формы
        setFormData({
          name: '',
          email: '',
          subject: '',
          message: ''
        });

        // Скрыть сообщение через 5 секунд
        setTimeout(() => {
          setSubmitMessage(null);
        }, 5000);
      }, 1500);
    }
  };

  return (
    <div className="contact-container">
      <h1 className="section-title">Контакты</h1>
      <p className="section-subtitle">Свяжитесь со мной</p>

      <div className="contact-content">
        <div className="contact-form-container">
          <div className="contact-info">
            <div className="contact-item">
              <i className="fas fa-envelope"></i>
              <a href="mailto:doroshov.daniil69@gmail.com">doroshov.daniil69@gmail.com</a>
            </div>
            <div className="contact-item">
              <i className="fab fa-telegram"></i>
              <a href="https://t.me/ananasDDA" target="_blank" rel="noopener noreferrer">@ananasDDA</a>
            </div>
            <div className="contact-item">
              <i className="fab fa-github"></i>
              <a href="https://github.com/ananasDDA" target="_blank" rel="noopener noreferrer">ananasDDA</a>
            </div>
          </div>

          <div className="contact-form">
            <h3>Напишите мне</h3>

            {submitMessage && (
              <div className={`submit-message ${submitMessage.type}`}>
                {submitMessage.text}
              </div>
            )}

            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="name">Имя</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className={formErrors.name ? 'error' : ''}
                />
                {formErrors.name && <span className="error-message">{formErrors.name}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className={formErrors.email ? 'error' : ''}
                />
                {formErrors.email && <span className="error-message">{formErrors.email}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="subject">Тема</label>
                <input
                  type="text"
                  id="subject"
                  name="subject"
                  value={formData.subject}
                  onChange={handleChange}
                  className={formErrors.subject ? 'error' : ''}
                />
                {formErrors.subject && <span className="error-message">{formErrors.subject}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="message">Сообщение</label>
                <textarea
                  id="message"
                  name="message"
                  rows="5"
                  value={formData.message}
                  onChange={handleChange}
                  className={formErrors.message ? 'error' : ''}
                ></textarea>
                {formErrors.message && <span className="error-message">{formErrors.message}</span>}
              </div>

              <button
                type="submit"
                className="btn btn-primary submit-btn"
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Отправка...' : 'Отправить сообщение'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
