import React, { useState } from 'react';
import './Experience.css';

const Experience = () => {
  const [selectedJob, setSelectedJob] = useState(null);

  // Данные о местах работы
  const jobExperience = [
    {
      id: 1,
      title: 'Инженер по тестированию ПО',
      company: 'ООО "Авроид"',
      location: 'Москва',
      period: 'Март 2024 - настоящее время',
      shortDescription: 'Тестирование и разработка решений для клиентов компании',
      responsibilities: [
        'Взаимодействие и консультации по техническим вопросам с отделами поддержки и разработки',
        'Поддержка и консультация важных клиентов компании и решение нетривиальных проблем',
        'Разработка и интеграция Telegram-решений для автоматизации рабочих процессов',
        'Проведение тестирования API с использованием Postman и разработка документации для API',
        'Создание и поддержка коллекции автотестов в Postman',
        'Автоматизация тестирования на уровне API с использованием Python и библиотеки requests',
        'Развертывание тестовых сред с использованием Docker и Aurora SDK',
        'Проведение технических собеседований'
      ],
      projects: [
        {
          name: 'Автоматизация рабочих процессов',
          description: 'Разработка Telegram-бота для автоматизации процессов компании',
          technologies: 'Python, Telegram API, Docker'
        },
        {
          name: 'Система тестирования API',
          description: 'Создание фреймворка для тестирования API с генерацией отчетов',
          technologies: 'Python, Postman, Jenkins'
        }
      ]
    },
    {
      id: 2,
      title: 'Инженер по тестированию ПО',
      company: 'ООО "Новые Облачные Технологии" ("МойОфис")',
      location: 'Москва',
      period: 'Январь 2023 - Март 2024',
      shortDescription: 'Тестирование продуктов компании на различных платформах',
      responsibilities: [
        'Проведение всех видов тестирования и работа на самых разных платформах (web, mac, win, iOS, Android, Linux, Aurora)',
        'Участие в разработке тестовой документации (тест-кейсы, чек-листы, отчеты о дефектах)',
        'Создание и поддержка автоматизированных тестов для web-приложений с использованием Python и Selenium WebDriver',
        'Проведение тестирования API с использованием Postman и разработка документации для API',
        'Создание документации для тестирования безопасности и проведение тестирования на уровне API',
        'Развертывание тестовых сред с использованием Docker и Kubernetes'
      ],
      projects: [
        {
          name: 'Автоматизация тестирования web-приложений',
          description: 'Разработка фреймворка автоматизации для продуктов МойОфис',
          technologies: 'Python, Selenium, Docker'
        },
        {
          name: 'Кроссплатформенное тестирование',
          description: 'Тестирование приложений на различных платформах и операционных системах',
          technologies: 'Postman, Docker, Kubernetes'
        }
      ],
      achievements: [
        'Стал самым молодым сотрудником компании',
        'Оптимизировал процесс тестирования для нескольких ключевых проектов'
      ]
    },
    {
      id: 3,
      title: 'Стажер',
      company: 'ООО "Новые Облачные Технологии" ("МойОфис")',
      location: 'Москва',
      period: 'Август 2022 - Январь 2023',
      shortDescription: 'Стажировка в отделе обеспечения качества',
      responsibilities: [
        'Освоение методологий тестирования и лучших практик обеспечения качества ПО',
        'Участие в тестировании производительности офисных приложений'
      ],
      achievements: [
        'Получил лучший результат на тестовом задании среди всех кандидатов'
      ]
    }
  ];

  // Открытие модального окна
  const openModal = (job) => {
    setSelectedJob(job);
    document.body.style.overflow = 'hidden'; // Блокируем прокрутку страницы
  };

  // Закрытие модального окна
  const closeModal = () => {
    setSelectedJob(null);
    document.body.style.overflow = 'auto'; // Возвращаем прокрутку страницы
  };

  return (
    <div className="experience-container">
      <h1 className="section-title">Опыт работы</h1>
      <p className="section-subtitle">Мой профессиональный путь</p>

      <div className="timeline">
        {jobExperience.map((job) => (
          <div
            className="timeline-item"
            key={job.id}
            onClick={() => openModal(job)}
          >
            <div className="timeline-dot"></div>
            <div className="timeline-date">{job.period}</div>
            <div className="timeline-content">
              <h3>{job.title}</h3>
              <h4>{job.company}</h4>
              <p>{job.shortDescription}</p>
              <div className="job-card-footer">
                <span className="click-hint">
                  <i className="fas fa-info-circle"></i> Нажмите для подробностей
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Модальное окно с подробной информацией */}
      {selectedJob && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="modal-close" onClick={closeModal}>
              <i className="fas fa-times"></i>
            </button>

            <div className="modal-header">
              <h2>{selectedJob.title}</h2>
              <h3>{selectedJob.company} | {selectedJob.location}</h3>
              <div className="modal-period">{selectedJob.period}</div>
            </div>

            <div className="modal-body">
              <div className="modal-section">
                <h4>
                  <i className="fas fa-tasks"></i> Обязанности и достижения
                </h4>
                <ul className="modal-list">
                  {selectedJob.responsibilities.map((responsibility, index) => (
                    <li key={index}>{responsibility}</li>
                  ))}

                  {selectedJob.achievements && (
                    <li className="achievements-section">
                      <strong>Достижения:</strong>
                      <ul>
                        {selectedJob.achievements.map((achievement, index) => (
                          <li key={index}>{achievement}</li>
                        ))}
                      </ul>
                    </li>
                  )}
                </ul>
              </div>

              {selectedJob.projects && selectedJob.projects.length > 0 && (
                <div className="modal-section">
                  <h4>
                    <i className="fas fa-project-diagram"></i> Проекты
                  </h4>
                  <div className="project-cards">
                    {selectedJob.projects.map((project, index) => (
                      <div className="project-card" key={index}>
                        <h5>{project.name}</h5>
                        <p>{project.description}</p>
                        <div className="project-tech">
                          <span className="tech-label">Технологии:</span> {project.technologies}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Experience;
