import React from 'react';
import './Portfolio.css';

const Portfolio = () => {
  const projects = [
    {
      id: 1,
      title: 'Telegram-бот для управления задачами',
      description: 'Бот для отслеживания и управления задачами с интеграцией с Jira и уведомлениями.',
      tech: ['Python', 'Aiogram', 'Jira API', 'SQLite'],
      image: '/images/portfolio/telegram-bot.jpg',
      github: 'https://github.com/ananasDDA/task-manager-bot',
      demo: null
    },
    {
      id: 2,
      title: 'Автоматизация тестирования веб-приложений',
      description: 'Фреймворк для автоматического тестирования веб-приложений с генерацией отчетов и интеграцией с CI/CD.',
      tech: ['Python', 'Selenium', 'Pytest', 'Allure', 'Jenkins'],
      image: '/images/portfolio/automation-framework.jpg',
      github: 'https://github.com/ananasDDA/web-testing-framework',
      demo: 'https://example.com/demo'
    },
    {
      id: 3,
      title: 'API для мониторинга производительности',
      description: 'RESTful API для сбора и анализа данных о производительности приложений.',
      tech: ['Python', 'FastAPI', 'PostgreSQL', 'Docker'],
      image: '/images/portfolio/api-monitoring.jpg',
      github: 'https://github.com/ananasDDA/performance-api',
      demo: null
    }
  ];

  return (
    <div className="portfolio-container">
      <h1 className="section-title">Портфолио</h1>
      <p className="section-subtitle">Мои проекты и работы</p>

      <div className="portfolio-content">
        <div className="projects-grid">
          {projects.map(project => (
            <div className="project-card" key={project.id}>
              <div className="project-image">
                <img src={project.image} alt={project.title} onError={(e) => { e.target.src = '/images/portfolio/placeholder.jpg' }} />
              </div>
              <div className="project-info">
                <h3 className="project-title">{project.title}</h3>
                <p className="project-description">{project.description}</p>
                <div className="project-tech">
                  {project.tech.map((tech, index) => (
                    <span className="tech-tag" key={index}>{tech}</span>
                  ))}
                </div>
                <div className="project-links">
                  {project.github && (
                    <a href={project.github} target="_blank" rel="noopener noreferrer" className="project-link">
                      <i className="fab fa-github"></i> GitHub
                    </a>
                  )}
                  {project.demo && (
                    <a href={project.demo} target="_blank" rel="noopener noreferrer" className="project-link">
                      <i className="fas fa-external-link-alt"></i> Демо
                    </a>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Portfolio;
