import React, { useState } from 'react';
import Modal from '../../components/Modal/Modal';
import useScrollAnimation from '../../hooks/useScrollAnimation';
import './Experience.css';

// Отдельный компонент для элемента временной шкалы
const TimelineItem = ({ job, onClick }) => {
  const [ref, isVisible] = useScrollAnimation(0.1);

  return (
    <div
      className={`timeline-item ${isVisible ? 'animate' : ''}`}
      ref={ref}
      onClick={() => onClick(job)}
    >
      <div className="timeline-dot"></div>
      <div className="timeline-date">{job.period}</div>
      <div className="timeline-content">
        <h3>{job.title}</h3>
        <h4>{job.company}</h4>
        <p>{job.description}</p>
        <div className="timeline-more">
          Нажмите для подробной информации
          <i className="fas fa-arrow-right"></i>
        </div>
      </div>
    </div>
  );
};

const Experience = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);

  // Данные о местах работы с проектами
  const jobData = [
    {
      id: 1,
      period: "2021 - Настоящее время",
      title: "Старший QA инженер",
      company: "Company XYZ",
      description: "Занимаюсь тестированием и автоматизацией проверок для нескольких проектов компании. Разрабатываю тестовые сценарии, проектирую и поддерживаю фреймворк автоматизации на Python.",
      responsibilities: [
        "Автоматизация тестирования на Python с использованием Pytest, Selenium",
        "Разработка и поддержка CI/CD пайплайнов",
        "Обеспечение качества продукта на всех этапах разработки",
        "Наставничество и обучение младших QA инженеров"
      ],
      projects: [
        {
          name: "Корпоративная платформа управления данными",
          description: "Разработка фреймворка автоматизации для тестирования web-интерфейса и REST API. Внедрение непрерывной интеграции с Jenkins для автоматического запуска тестов при каждом коммите.",
          technologies: ["Python", "Pytest", "Selenium", "Jenkins", "Docker", "Allure"]
        },
        {
          name: "Мобильное приложение для финансового сектора",
          description: "Создание стратегии тестирования и автоматизация проверок для iOS и Android приложений. Внедрение BDD-подхода с использованием Cucumber и Appium.",
          technologies: ["Appium", "Java", "Cucumber", "TestNG", "BDD"]
        },
        {
          name: "Интеграция платежных систем",
          description: "Разработка и автоматизация тестовых сценариев для проверки интеграции с различными платежными системами. Тестирование безопасности и производительности API.",
          technologies: ["Postman", "JMeter", "Python", "Security Testing"]
        }
      ]
    },
    {
      id: 2,
      period: "2019 - 2021",
      title: "QA инженер",
      company: "Tech Solutions Inc.",
      description: "Участвовал в разработке и тестировании веб-приложений и мобильных приложений. Проводил ручное и автоматизированное тестирование, работал с баг-трекерами.",
      responsibilities: [
        "Функциональное, регрессионное и UI тестирование",
        "Разработка тестовых сценариев и тест-кейсов",
        "Базовая автоматизация с использованием Python и Selenium",
        "Работа с REST API и Postman"
      ],
      projects: [
        {
          name: "CRM система для компаний малого бизнеса",
          description: "Тестирование веб-интерфейса CRM системы, включая функциональные, интеграционные и регрессионные тесты. Автоматизация критических пользовательских сценариев.",
          technologies: ["Selenium", "JavaScript", "TestRail", "JIRA"]
        },
        {
          name: "Веб-портал для образовательной платформы",
          description: "Проверка соответствия требованиям и совместимости с различными браузерами. Создание автоматизированных тестов для проверки процесса регистрации и авторизации.",
          technologies: ["Selenium WebDriver", "Python", "Cross-browser Testing"]
        }
      ]
    },
    {
      id: 3,
      period: "2018 - 2019",
      title: "Стажер-тестировщик",
      company: "Digital Start",
      description: "Начал свою карьеру в IT с позиции стажера-тестировщика. Изучал основы тестирования ПО, проводил базовые проверки и писал тестовую документацию.",
      responsibilities: [
        "Ручное тестирование веб-приложений",
        "Работа с тестовой документацией",
        "Изучение основных принципов QA",
        "Участие в командных обсуждениях и ретроспективах"
      ],
      projects: [
        {
          name: "Интернет-магазин электроники",
          description: "Ручное тестирование функциональности и пользовательского интерфейса. Подготовка тестовой документации и отчетов о дефектах.",
          technologies: ["Manual Testing", "Test Case Design", "Bug Tracking"]
        }
      ]
    }
  ];

  const openModal = (job) => {
    setSelectedJob(job);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  // Создание содержимого модального окна для выбранной работы
  const modalContent = selectedJob ? (
    <div className="modal-job-content">
      <div className="modal-job-overview">
        <p className="modal-job-description">{selectedJob.description}</p>

        <div className="modal-responsibilities">
          <h4>Основные обязанности:</h4>
          <ul>
            {selectedJob.responsibilities.map((resp, index) => (
              <li key={index}>{resp}</li>
            ))}
          </ul>
        </div>
      </div>

      <h3 className="modal-projects-title">Ключевые проекты:</h3>

      {selectedJob.projects.map((project, index) => (
        <div className="modal-project" key={index}>
          <h3>{project.name}</h3>
          <p className="modal-project-description">{project.description}</p>

          <div className="modal-project-tech">
            <div className="modal-tech-title">Технологии:</div>
            <div className="modal-tech-tags">
              {project.technologies.map((tech, techIndex) => (
                <span className="modal-tech-tag" key={techIndex}>{tech}</span>
              ))}
            </div>
          </div>
        </div>
      ))}
    </div>
  ) : null;

  return (
    <div className="experience-container">
      <h1 className="section-title">Опыт работы</h1>
      <p className="section-subtitle">Мой профессиональный путь</p>

      <div className="timeline">
        {jobData.map((job) => (
          <TimelineItem
            key={job.id}
            job={job}
            onClick={openModal}
          />
        ))}
      </div>

      {/* Модальное окно */}
      <Modal
        isOpen={isModalOpen}
        onClose={closeModal}
        title={selectedJob ? `${selectedJob.title} в ${selectedJob.company}` : ''}
        content={modalContent}
      />
    </div>
  );
};

export default Experience;
